void calc_dnums(size_t range)
{
	int p1 = 0;
	int p2 = 0;
	for (size_t i = 220; i <= range; i++)
	{
		p1 = 0;
		p2 = 0;
		for(int k=1; k<i; k++)  if(i%k==0) p1 += k;
		if (i >= p1) continue;
		for(int k=1; k<p1; k++) if(p1%k==0) p2 += k;
		if(p2 == i)
			printf("%d %d\n", i, p1);
	}

};

